mapper.select:

select admin_id, username, password, website, create_time, last_update, expiry_time
from admin
where admin_id = #{adminId,jdbcType=INTEGER}

select admin_id, username, password, website, create_time, last_update, expiry_time
from admin
where 1=1
and username = #{username,jdbcType=VARCHAR}
and password = #{password,jdbcType=VARCHAR}
and website = #{website,jdbcType=VARCHAR}
and create_time = #{createTime,jdbcType=DATE}
and last_update = #{lastUpdate,jdbcType=DATE}
and expiry_time = #{expiryTime,jdbcType=DATE}
