mapper.update:

update goods_kind
set goods_kind_name = #{goodsKindName,jdbcType=VARCHAR},
  goods_kind_flag = #{goodsKindFlag,jdbcType=CHAR},
  admin_id = #{adminId,jdbcType=INTEGER}
where goods_kind_id = #{goodsKindId,jdbcType=INTEGER}

mapper.select:

select goods_kind_id, goods_kind_name, goods_kind_flag, admin_id
from goods_kind
where goods_kind_id = #{goodsKindId,jdbcType=INTEGER}

select goods_kind_id, goods_kind_name, goods_kind_flag, admin_id
from goods_kind
where 1=1
and goods_kind_name = #{goodsKindName,jdbcType=VARCHAR}
and goods_kind_flag = #{goodsKindFlag,jdbcType=CHAR}
and admin_id = #{adminId,jdbcType=INTEGER}
